select a.regist_id from jpush_regist_bind a inner join (
	SELECT empcode from om_employee where JOBLEVEL in

		#{joblevel}

	AND EMPSTATUS = 'on'
) b on a.user_id = b.empcode

select a.regist_id from jpush_regist_bind a inner join (
	SELECT empcode from om_employee where JOBNAME in

		#{jobname}

	AND EMPSTATUS = 'on'
) b on a.user_id = b.empcode
