mapper.update:
update apply_store set
down_load_count=down_load_count+1
where id =#{id}

mapper.select:
select a.id,a.down_load_count,a.size,a.auto_update,a.en_name,a.cn_name, a.status,a.partner_permission,a.summary,a.create_time,a.default_app, a.version_type,a.has_resources,a.app_content,a.app_file,a.app_order, a.ios_appVersion,a.android_appVersion,a.download_line,a.download_url, b.id bid,b.appId,b.os_type,b.judge_symbol,b.app_version from apply_store a left join app_auto_refresh_control b on a.id = b.appId
1 = 1
and a.id=#{appId}
order by a.app_order limit #{begin},#{pageSize}

select id,device_token,apply_store_id,version_type,os_type,remark,update_time FROM apply_store_device
1 = 1
and device_token=#{deviceToken}
and apply_store_id=#{applyStoreId}
limit 0,200

mapper.delete:
  	 	DELETE FROM APPLY_STORE_DEVICE 

			 device_token=#{deviceToken} 
			 AND apply_store_id IN (
				 SELECT t.id  FROM  apply_store t  
				 WHERE  t.id IN 

					#{id}

				AND t.default_app != '0'
			)

  	 	DELETE FROM APPLY_STORE_DEVICE 

			 device_token=#{deviceToken} 
			 AND apply_store_id not IN (
				 SELECT t.id FROM apply_store t 
				 WHERE t.default_app = '0'
			 )

mapper.update:
    UPDATE APPLY_STORE_DEVICE 


        version_type = #{versionType},


        os_type = #{osType},


        remark = #{remark}



		 apply_store_id = #{applyStoreId} and device_token=#{deviceToken}

mapper.select:
  		SELECT
			version_type
		FROM
			apply_store_device
		WHERE
			apply_store_id = #{appId}
		AND device_token = #{deviceToken}
		AND os_type = #{osType}
		LIMIT 1
